sys_script_fix.script:
var mcRecord = new GlideRecord('x_58872_needit_monitoring_configuration');

mcRecord.addQuery('password','!=','');
mcRecord.query();

while(mcRecord.next()){
	var mcUser = mcRecord.username;
	var mcPassword = mcRecord.password;
	var decrypt = mcPassword.getDecryptedValue();

	if(decrypt == "*****"){
		mcPassword.setDisplayValue("5 Star Found");
		mcRecord.update();
		gs.info("5 Star Found for " + mcUser + "! Password has been updated.");
	}
	else{
		gs.info("decrypted password for " + mcUser + ": " + decrypt);
	}
}
